'use strict'

module.exports=loop;

//function to do the async loop operation
function loop(data,array,customFunction,callbackFunction){
    if((typeof customFunction === "function")&&(typeof callbackFunction === "function")){
        array.forEach(function(key,i){
            customFunction(data,key);
            if((i+1)===array.length){
                callbackFunction(data)
            }
        })
    }
    else{
        console.log("Last two parameters should be of type function.")
    }
}